import axios from 'axios'
import { useState, useEffect } from 'react';
import React from 'react'

const SKDetailModal = ({ showModal, setShowModal, id }) => {
    const [suratKeluar, setSk] = useState(null);
    const [visibility, setVisibility] = useState('');

    useEffect(() => {
        sk();
    })

    const sk = async () => {
        const getSk = await axios.get(`http://localhost:3000/suratkeluar/${id}`);
        setSk(getSk)
    } 

    return ( 
        <>
        { 
            showModal ? (
                <div class="modal is-active">
                    <div class="modal-background"></div>
                    <div class="modal-content">
                        {
                            <div>
                                <h3>{ suratKeluar.nomor }</h3>
                                <h3>{ suratKeluar.tanggal }</h3>
                                <h3>{ suratKeluar.keterangan }</h3>
                                <h3>{ suratKeluar.kepada }</h3>
                            </div>
                        }
                    </div>
                    <button class="modal-close is-large" aria-label="close"></button>
                </div>
            ) : null 
        }
        </>
    )
}

export default SKDetailModal
