import axios from 'axios'
import { useState, useEffect } from 'react';
import React from 'react'

const SKDetailModal = ({ showModal, setShowModal, id }) => {
    const [suratKeluar, setSk] = useState(null);

    useEffect(() => {
        sk();
    })

    const sk = async () => {
        const getSk = await axios.get(`http://localhost:3000/suratkeluar/${id}`);
        setSk(getSk.data)
    } 

    return ( 
        <>
        { 
            showModal ? (
                <div class="modal is-active">
                    <div class="modal-background"></div>
                    <div class="modal-content">
                        {
                            <div>
                                <h1>Hello</h1>
                                <h1>{ suratKeluar.nomor }</h1>
                                <h1>{ suratKeluar.tanggal }</h1>
                                <h1>{ suratKeluar.keterangan }</h1>
                                <h1>{ suratKeluar.kepada }</h1>
                            </div>
                        }
                    </div>
                    <button class="modal-close is-large" aria-label="close" onClick = {() => setShowModal(prev => !prev)}></button>
                </div>
            ) : null 
        }
        </>
    )
}

export default SKDetailModal
